const saveGoodInCart = (localStorageCart, data, ind) => {
  if (ind === -1) {
    const newObj = {
      id: data.id,
      count: 1,
      price: data.price,
      discount: data.discount
    };
    localStorageCart.push(newObj);
  } else {
    localStorageCart[ind].count++;
  }
  addInLocalStorage(localStorageCart);
};
